import { MYNAME } from '../actionType';
const initialState = {
  name: 'sid'
};

const introReducer = (state = initialState, action) => {
  switch (action.type) {
    case MYNAME:
      return Object.assign({}, state, {
        name: action.payload
      });

    default:
      return state;
  }
};

export default introReducer;
